import "../App.css"
import {formatISO9075} from "date"


const Post = ({title,summary,cover,content,createdAt}) => {
  
  return (
    <div className="post">
      <div className="image">
        <img src={cover} />
      </div>

      <div className="texts">
        <h2>{title}</h2>
        <p className="info">
          <span className="author">Vishwas Nalawade </span>

          <time>{formatI}</time>
        </p>

        <p className="summary">
         {summary}
        </p>
      </div>
    </div>
  );
};

export default Post;
